import './index.scss'
import AnimatedLetters from '../AnimatedLetters'
import { useEffect, useState } from 'react'

const About = () => {
    const [letterClass, setLetterClass] = useState('text-animate')

    useEffect(() => {
        return setTimeout(() => {
            setLetterClass('text-animate-hover')
        }, 2500)
    }, [])
    return (
        <div className='container about-page'>
            <div className='text-zone'>
                <h1>
                    <AnimatedLetters
                        strArray={'About me'.split('')}
                        idx={15}
                        letterClass={letterClass}
                    />
                </h1>
                <p>
                    I am a reliable and hard-working programmer with 4 years of experience, recognized with awards for my accomplishments. I consider myself a creative, out-of-the-box thinker who is self-motivated and enthusiastic about collaborating with others.
                </p>
                <p>
                    Being incredibly passionate about programming, I am constantly driven to enhance my skills through formal coursework and self-directed study.
                </p>
                <p>
                    Participating in a host of different extracurricular activities, I pride myself on my time management skills and scheduling abilities.
                </p>

            </div>

        </div>
    )
}

export default About
